#!/bin/bash
# Parallel network scanner for SSH services

scan_subnet() {
    subnet=$1
    echo "Scanning $subnet.0/24..."
    nmap -n -p 22,2222,22222 --open -T4 --min-rate 1000 192.168.$subnet.0/24 --exclude 192.168.122.1 2>/dev/null | grep -B4 "open" | grep "Nmap scan report" | awk '{print $5}'
}

export -f scan_subnet

# Scan multiple subnets in parallel
seq 0 255 | xargs -P 20 -I {} bash -c 'scan_subnet {}'
