{"file_name": "flag_sniffer.py", "content": "#!/usr/bin/env python3
from scapy.all import sniff, IP, TCP, UDP, Raw

found_flag = []

def process_packet(packet):
    if packet.haslayer(Raw):
        payload = packet[Raw].load
        if isinstance(payload, bytes):
            payload_str = payload.decode('utf-8', errors='ignore')
            if 'CTF FLAG:' in payload_str:
                start_idx = payload_str.find('CTF FLAG:')
                flag_part = payload_str[start_idx:]
                found_flag.append(flag_part)
                print(f"[+] Flag found: {flag_part}")
                return True
    return False

def stop_filter(packet):
    return process_packet(packet)

print("[*] Starting packet capture...")
print("[*] Waiting for packets containing the flag...")

sniff(iface="eth0", prn=lambda x: None, stop_filter=stop_filter, store=0)

if found_flag:
    print(f"\n[+] Capture complete!")
    print(f"[+] Flag: {found_flag[0]}")
"}

